import React, { useState } from 'react';
import './DualPopup.css'; // Assurez-vous de créer ce fichier CSS pour styliser votre composant

function DualPopup() {
  const [activeItems, setActiveItems] = useState([]);
  const items = [
    { id: 1, text: 'Élément 1' },
    { id: 2, text: 'Élément 2' },
    { id: 3, text: 'Élément 3' },
    { id: 4, text: 'Élément 4' },
    // Ajoutez d'autres éléments si nécessaire
  ];

  const toggleActiveItem = (item) => {
    if (activeItems.includes(item)) {
      // Désactiver l'élément s'il est déjà actif
      setActiveItems(activeItems.filter((activeItem) => activeItem !== item));
    } else {
      // Activer l'élément s'il n'est pas encore actif
      setActiveItems([...activeItems, item]);
    }
  };

  return (
    <div className="dual-popup">
      <button onClick={() => toggleActiveItem(1)}>Activer/Désactiver Élément 1</button>
      <button onClick={() => toggleActiveItem(2)}>Activer/Désactiver Élément 2</button>
      <div className="popup-content">
        <h2>Éléments Actifs</h2>
        <ul>
          {items.map((item) => (
            <li key={item.id} className={activeItems.includes(item.id) ? 'active' : ''}>
              {item.text}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default DualPopup;
