import './App.css';
import { React, useState } from 'react'
// import Footer from './components/Footer';
// import React, {useState} from 'react'
import { BrowserRouter, Routes, Route} from 'react-router-dom';
// import {useState,React} from 'react'
import Accueil from './components/Accueil';
import Services from './components/Services';
import Apropos from './components/Apropos';
import Contact from './components/Contact';
import Curriculum_Vitae from './components/Curriculum_Vitae';
import Exploits from './components/Exploits';
import Profile from './components/Profile';
import Loading from './components/Loading';
// import DarkModeToggle from './pages/DarkModeToggle';


function App() {
  const [showButton, setShowButton] = useState(false);

const handleScroll = () => {
  if (window.scrollY > 100) {
    setShowButton(true);
  } else {
    setShowButton(false);
  }
};

const scrollToTop = () => {
  window.scrollTo({
    top: 0,
    behavior: 'smooth'
  });
};

// Attach the scroll event listener
window.addEventListener('scroll', handleScroll);
  return (
    <>
    <div>
     <BrowserRouter>
      <Routes>
      <Route path="/" Component={Accueil} />
      <Route path="/services" Component={Services} />
      <Route path="/apropos" Component={Apropos} />
      <Route path="/contact" Component={Contact} />
      <Route path="/cv" Component={Curriculum_Vitae} />
      <Route path="/exploits" Component={Exploits} />
      <Route path="/profile" Component={Profile} />
      <Route path="/Loading" Component={Loading} />
      </Routes>
    </BrowserRouter>
    {/* <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <div className="container">
          <a className="navbar-brand" href="/">
            My App
          </a>
          <DarkModeToggle />
        </div>
      </nav>
    </div> */}
                            {/* Scroll to top button */}
         {showButton && (
        <button className="scroll-to-top-button" onClick={scrollToTop}>
          Scroll to Top
        </button>
      )}

    </div>
    </>
  );
};

export default App;
