import './App.css';
import { React, useState } from 'react'
// import Footer from './components/Footer';
// import React, {useState} from 'react'
import { BrowserRouter, Routes, Route} from 'react-router-dom';
// import {useState,React} from 'react'
import Accueil from './components/Accueil';
import Services from './components/Services';
import Apropos from './components/Apropos';
import Contact from './components/Contact';
import Curriculum_Vitae from './components/Curriculum_Vitae';
import Exploits from './components/Exploits';
import Profile from './components/Profile';
import Loading from './components/Loading';
// import DarkModeToggle from './pages/DarkModeToggle';


function App() {
  const [showButton, setShowButton] = useState(false);

const handleScroll = () => {
  if (window.scrollY > 100) {
    setShowButton(true);
  } else {
    setShowButton(false);
  }
};

const scrollToTop = () => {
  window.scrollTo({
    top: 0,
    behavior: 'smooth'
  });
};

// Attach the scroll event listener
window.addEventListener('scroll', handleScroll);
const [isDarkMode, setIsDarkMode] = useState(false);
  useEffect(() => {
       //body all
        const body = document.body;
        const div = document.querySelector('div');
        //navbar
        const moon = document.getElementById("moon");
        const sun = document.getElementById("sun");
        //home
        if (isDarkMode) {
          body.classList.add('dark-mode');
          moon.style.display ="none";
          sun.style.display ="block";
          div.classList.add('dark-mode1');
        } else {
          body.classList.remove('dark-mode');
          moon.style.display ="block";
          sun.style.display ="none";
          div.classList.remove('dark-mode1');
        }
      }, [isDarkMode]);

      const toggleDarkMode = () => {
        setIsDarkMode(prevMode => !prevMode);
      };
  return (
    <>
    <div>
    <article className='nav-4'>
                <i className='bi-moon-stars fs-4' id="moon" onClick={toggleDarkMode}></i>
                <i className='bi-sun fs-4' id="sun" onClick={toggleDarkMode}></i>
              </article>
     <BrowserRouter>
      <Routes>
      <Route path="/" Component={Accueil} />
      <Route path="/services" Component={Services} />
      <Route path="/apropos" Component={Apropos} />
      <Route path="/contact" Component={Contact} />
      <Route path="/cv" Component={Curriculum_Vitae} />
      <Route path="/exploits" Component={Exploits} />
      <Route path="/profile" Component={Profile} />
      <Route path="/Loading" Component={Loading} />
      </Routes>
    </BrowserRouter>
         {/* Scroll to top button */}
         {showButton && (
        <button className="scroll-to-top-button rounded-circle shadow-sm float-end" onClick={scrollToTop}>
          <i className='bi-chevron-up fs-5'></i>
        </button>
      )}

    </div>
    </>
  );
};

export default App;
