import React, { useState } from 'react';
import './SlidingTable.css'; // Assurez-vous de créer ce fichier CSS pour styliser votre composant

function SlidingTable() {
  const [activeIndex, setActiveIndex] = useState(0);

  const items = [
    'Item 1',
    'Item 2',
    'Item 3',
    'Item 4',
    'Item 5',
    'Item 6',
    // Ajoutez autant d'éléments que nécessaire
  ];

  const handleItemClick = (index) => {
    setActiveIndex(index);
  };

  return (
    <div className="sliding-table">
      <div className="table-container">
        <ul className="table">
          {items.map((item, index) => (
            <li
              key={index}
              className={`table-item ${index === activeIndex ? 'active' : ''}`}
              onClick={() => handleItemClick(index)}
            >
              {item}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default SlidingTable;
